CREATE TABLE dbo.LeagueResults(	id INT NOT NULL,	userteam_name NVARCHAR(100) NOT NULL,	gameweek_total INT NOT NULL,	player_name NVARCHAR(100) NOT NULL,	movement NVARCHAR(104) NOT NULL,	is_own_entry BIT NOT NULL,	league_rank INT NOT NULL,	last_rank INT NOT NULL,	rank_sort INT NOT NULL,	total INT NOT NULL,	userteamid INT NOT NULL,	leagueid INT NOT NULL,	start_gameweekid INT NOT NULL,	stop_gameweekid INT NOT NULL,	CONSTRAINT PK_LeagueResults PRIMARY KEY CLUSTERED (id ASC));