CREATE TABLE dbo.League(	id INT NOT NULL,	league_name NVARCHAR(100) NOT NULL,	league_short_name NVARCHAR(100) NULL,	created SMALLDATETIME NOT NULL,	is_closed BIT NOT NULL,	is_forum_disabled BIT NOT NULL,	is_make_code_public BIT NOT NULL,	league_rank INT NULL,	league_size int NULL,	league_type CHAR(1) NOT NULL,	scoring CHAR(1) NOT NULL,	is_reprocess_standings BIT NOT NULL,	admin_userteamid INT NULL,	start_gameweekid INT NOT NULL,	CONSTRAINT PK_League PRIMARY KEY CLUSTERED (id ASC));